import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { Header, TaskContainer, TaskDetailModal, AddTaskModal } from "./containers";
import { TaskInterface, TaskDataInterface } from "../../shared/TaskInterface.ts";

function App() {
