    const [taskData, setTaskData] = React.useState<TaskInterface | null>(null);

    const [showTaskDetailModal, setShowTaskDetailModal] = React.useState(false);
    const [taskDetailModalContent, setTaskDetailModalContent] = React.useState({});

    const openTaskDetailModal = function (task: TaskInterface) {
        setShowTaskDetailModal(true);
        setTaskData(task);
    };

    const closeTaskDetailModal = () => {
        setShowTaskDetailModal(false);
    };

    return (
        <div className="App">
            {/* <Form /> */}
            <Header />
            <TaskDetailModal show={showTaskDetailModal} onHide={closeTaskDetailModal} task={taskData} />
            {/* <AddTaskModal onHide={closeAddModal} /> */}
            <TaskContainer openAddModal={() => {}} openTaskModal={openTaskDetailModal} />
        </div>
    );
}

export default App;
