import React from "react";
import TaskInterface from "../../TaskInterface";
import { addModalBodyStyles, removeModalBodyStyles } from "../utils/utils";

interface ModalProps extends TaskInterface {
    show: boolean;
    onHide: () => void;
}

function TaskDetailModal({ show, onHide, name, short_description, task_body, completed, important }: ModalProps) {
    const [isOpen, setIsOpen] = React.useState(false);

    React.useEffect(
        function () {
            addModalBodyStyles();
        },
        [isOpen]
    );

    return (
        <div
            className={`modal ${show ? "show" : ""} fade modal-dialog modal-dialog-centered modal-dialog-scrollable`}
            id="exampleModal"
            tabIndex={-1}
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
        >
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h1 className="modal-title fs-5 flex-grow-1" id="exampleModalLabel">
                            Model Task
                        </h1>
                        <p>important</p>
                        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                        <h2>This is a model task</h2>
                        <p>
                            This is a model task with Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quae vero aperiam voluptatem sed quia consequuntur modi, ipsam repellat animi vel,
                            nostrum ea obcaecati magni.
                        </p>
                    </div>
                    <div className="modal-footer justify-content-between flex-nowrap overflow-scroll">
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                            <label className="form-check-label" htmlFor="flexCheckDefault">
                                Task Completed
                            </label>
                        </div>

                        <div>
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={onHide}>
                                Close
                            </button>
                            <button type="button" className="btn btn-danger">
                                Delete
                            </button>
                            <button type="button" className="btn btn-primary">
                                Save changes
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TaskDetailModal;
